/************************************************************************ 
*                           Variables Globales   
************************************************************************/
${declarations}


/*main*/
int main(void) {

	InitSystem();

    while(1) {  

           PORTB ^= 0xFF;
			_delay_ms(500);
    }

    return 0;
}

void InitSystem() {

	DDRB = 0XFF;
	PORTB = 0x00;

}
